var y = Math.floor(Math.random() * (document.body.parentNode.clientHeight - 200)) + document.body.parentNode.scrollTop;

if (document.DanceContextletQueue == undefined || !document.DanceContextletQueue.length) {
    document.DanceContextletQueue = imageURLs.slice().sort(function (a, b) {
        return Math.random() < .5 ? 1 : -1;
    });
}

var img = document.createElement('img');
img.src = document.DanceContextletQueue.pop();
img.style.MozUserSelect = 'none';
img.style.userSelect = 'none';
img.style.position = 'absolute';
img.style.left = x + 'px';
img.style.top = y + 'px';
img.style.zIndex = 2147483647;

img.addEventListener('dragstart', function (e) {
    e.preventDefault();
});

img.addEventListener('mousedown', function (e) {
    startX = e.clientX;
    startY = e.clientY;
    dragging = true;
}, false);

document.addEventListener('mousemove', function (e) {
    if (dragging) {
        x += e.clientX - startX;
        y += e.clientY - startY;
        startX = e.clientX;
        startY = e.clientY;
        img.style.left = x + 'px';
        img.style.top = y + 'px';
    }
}, false);

document.addEventListener('mouseup', function (e) {
    dragging = false;
}, false);

