var url = 'https://en.wikipedia.org/wiki/Special:Search?search=' + encodeURIComponent(this.info.selectionText);

if (this.info.modifiers.indexOf('Ctrl') == -1)
{
    // Open in the same tab.

    document.location = url;
}
else
{
    // Open in a new tab.

    // window.open() is subject to the site's popup blocker settings, so we'll use a background script instead.

    this.runAs('background', function ()
    {
        browser.tabs.create({url: this.params.url, active: true});
    }, {url: url});
}
